	src,
	thumbsrc
) values (
	#{spaceIdx},
	#{src},
	#{thumbSrc}
)

mapper.select:
select spaceidx, midx
from liked_spaces
where spaceidx = #{idx}

mapper.insert:
insert into liked_spaces (
	spaceidx,
	midx
) values (
	#{spaceIdx},
	#{mIdx}
)

mapper.update:
update spaces
set status = 1
where idx = #{idx}

update spaces
set status = 2
where idx = #{idx}

update spaces
set status = 0
where idx = #{idx}

mapper.select:
select * from ( select a.*, count(ls.spaceidx) as liked from ( select s.*, count(sr.score) as reviewCnt, avg(sr.score) as reviewAvg from space_review sr right join spaces s on sr.spaceidx = s.idx group by s.idx ) a left join (select * from liked_spaces where midx = #{likedMidx}) ls on a.idx = ls.spaceidx group by a.idx ) aa where status = 1
and addr1 = #{vo.addr1}
and addr2 = #{vo.addr2}
and type = #{vo.type}
and name like concat('%', #{vo.name}, '%')
and liked = 1
order by reviewCnt desc
order by reviewAvg desc
limit #{start}, 12

select count(*) from reservations
where startdate = #{startDate}
and enddate = #{endDate}
and spaceidx = #{spaceIdx}
and rsvstatus = 0

select date from (
	select date(startdate) as date, sum(rsvhours) as rsvhours
	from reservations
	where spaceidx = #{spaceIdx} and date(startdate) between #{nowDate} and #{afterMonth}
	and rsvstatus = 0
	group by date
	) a	where a.rsvhours = 15;

select startDate, endDate from reservations
where spaceidx = #{spaceIdx} and date(startdate) = #{date}
and rsvstatus = 0

select count(*)
from reservations a, spaces b
where a.spaceidx = b.idx
and a.midx = #{mIdx}



			and date(a.resdate) = #{start}
			order by resdate


			and date(a.resdate) >= #{start}
			and date(a.resdate) <= #{end}
			order by resdate




			and date(a.startdate) = #{start}
			order by startdate


			and date(a.startdate) >= #{start}
			and date(a.startdate) <= #{end}
			order by startdate

mapper.update:
update space_review set
	picturesrc = #{pictureSrc},
	thumbsrc = #{thumbSrc},
	score = #{score},
	content = #{content}
where residx = #{resIdx}

update space_qna set
	content = #{content},
	publicyn = #{publicYN}
where qnaidx = #{qnaIdx}

mapper.select:
select count(*)
from reservations a, spaces b
where a.spaceidx = b.idx
and a.spaceidx = #{spaceIdx}



			and date(a.resdate) = #{start}
			order by resdate


			and date(a.resdate) >= #{start}
			and date(a.resdate) <= #{end}
			order by resdate




			and date(a.startdate) = #{start}
			order by startdate


			and date(a.startdate) >= #{start}
			and date(a.startdate) <= #{end}
			order by startdate

select count(*) from points p
inner join reservations r
on p.resIdx = r.residx
where p.midx = #{mIdx}


		and date(r.resdate) = #{start}
		order by resdate


		and date(r.resdate) >= #{start}
		and date(r.resdate) <= #{end}
		order by resdate

select p.*, r.resdate from points p
inner join reservations r
on p.resIdx = r.residx
where p.midx = #{mIdx}


		and date(r.resdate) = #{start}


		and date(r.resdate) >= #{start}
		and date(r.resdate) <= #{end}


order by pointidx desc
limit #{startRow}, 10

mapper.update:
